<style>
  .hero {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 auto;
    padding: clamp(64px, 20vw, 196px) 32px 32px;
    overflow: auto;
  }

  .hero > * {
    text-align: center;
  }

  .hero h1 {
    margin: 0;
    color: transparent;
    background: linear-gradient(to right, var(--color), var(--accent));
    -webkit-background-clip: text;
    background-clip: text;
    font-size: clamp(48px, 10vw, 96px);
  }

  .hero p {
    font-size: clamp(16px, 2.5vw, 24px);
  }

  .hero p span {
    display: inline-block;
  }

  .buttons {
    display: flex;
    gap: 8px;
  }

  .sect {
    padding-top: 32px;
    padding-bottom: 32px;
  }

  .preview {
    margin: 64px 0;
    aspect-ratio: 16 / 9;
    font-size: 0;
  }

  .preview * {
    width: 100%;
    height: 100%;
    border: 2px solid var(--accent);
    border-radius: 16px;
    box-shadow: 0 0 16px 0 var(--accent);
  }

  .parallax {
    height: 25vh;
    overflow: hidden;
  }

  .parallax h1 {
    margin: 0;
    padding: 64px;
    text-align: center;
    font-size: clamp(64px, 15vw, 128px);
  }

  .highlight {
    --dim: color-mix(in oklab, var(--color), transparent 90%);
    width: fit-content;
    color: transparent;
    background: linear-gradient(
        to right,
        var(--color) 0%,
        var(--color) 50%,
        var(--dim) 50%,
        var(--dim) 100%
      )
      100% 0 / 200% 100%;
    -webkit-background-clip: text;
    background-clip: text;
